INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `content_loc9`, `content_loc10`) VALUES
	(2000000104, '... oops.', NULL, NULL, '... Uups.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000077, '%s gestures to the pitcher of water sitting on the edge of the well.', NULL, NULL, 'Ah, das ist genau richtig! Ich glaube, ich hole mir noch ein paar... könnt ihr für mich auf diese Fässer aufpassen, $N?', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000102, 'Oh, darn...', NULL, NULL, 'Oh, verdammt...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000103, 'Ok, let\'s get started!', NULL, NULL, 'Na gut! Dann mal los!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000033, 'Keep moving!', NULL, NULL, 'Weitergehen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000034, 'Come, $N. Lord Stormrage awaits.', NULL, NULL, 'Kommt, $N. Fürst Sturmgrimm erwartet Euch.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000035, 'Warriors of Dragonmaw, gather \'round! One among you has attained the rank of highlord! Bow your heads in reverence! Show your respect and allegiance to Highlord $N!', NULL, NULL, 'Krieger des Drachenmals, schart euch zusammen! Einer unter euch hat den Rang des Hochlords erreicht! Verneigt euch aus Ehrfurcht! Zeigt eure Hochachtung und Loyalität gegenüber Hochlord $N!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000036, 'Lord Illidan will be here shortly.', NULL, NULL, 'Fürst Illidan wird hier bald eintreffen.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000037, 'All hail Lord Illidan!', NULL, NULL, 'Es lebe Fürst Illidan!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000038, 'Lord Illidan, this is the Dragonmaw that I, and others, have told you about. He will lead us to victory!', NULL, NULL, 'Fürst Illidan, dies ist das Drachenmal über das ich, und andere, euch erzählt haben. Es wird uns zum Sieg verhelfen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000039, 'But... My lord, I do not understand. $N... He is the orc that has...', NULL, NULL, 'Aber... Mein Fürst, ich verstehe nicht. $N... Er ist der Ork, der...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000040, 'It will be done, my lord.', NULL, NULL, 'Es wird vollbracht, mein Fürst.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000041, 'So you thought to make a fool of Mor\'ghor, eh? Before you are delivered to Lord Illidan, you will feel pain that you could not know to exist. I will take pleasure in exacting my own vengeance.', NULL, NULL, 'Ihr dachtet also Ihr könntet Mor\'ghor zum Narren halten, wie? Bevor Ihr Fürst Illidan übergeben werdet, werdet Ihr Schmerzen fühlen, deren Existenz Euch bisher unbekannt war. Mit Freuden werde ich meine Rache auskosten.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000045, 'There\'s nothing like some scalding mornbrew on a chilly Dun Morogh day to get things started right!', NULL, NULL, 'Es geht doch nichts über kochend heißes Morgengebräu an einem kalten Morgen in Dun Morogh! Danach kann der Tag kommen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000046, 'Oooooo hot hot hot! If that won\'t put spring in your step, I don\'t know what will!', NULL, NULL, 'Ooooh! Heiß! Heiß! Heiß! Wenn Euch das nicht auf Touren bringt, kann ich Euch auch nicht helfen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000047, 'Ribbit! No!! This cannot...ribbit...be! You have duped me with...ribbit...your foul trickery! Ribbit!', NULL, NULL, 'Verflixt! Nein!!! Das kann... verflixt... nicht sein. Ihr habt mich mit Euren faulen Tricks betrogen. Verflixt', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000048, 'Now let us place this rare earth in my planter...', NULL, NULL, 'Nun lasst uns diese Erde in meinen Pflanztopf stecken...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000049, 'Thank you again, $N. This rare earth will be very helpful in my experiments.', NULL, NULL, 'Vielen Dank nochmal, $N. Diese Rare Erde wird mir bei meinen Experimenten sehr nützlich sein.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000055, 'How many more of you grunts do I need to send back to mommy and daddy in a body bag before you grow a brain and realize that runnin\' head first into a Legion kill squad is suicide? And don\'t nod your thick skulls at me as if you know what I\'m talkin\' about!', NULL, NULL, 'Wieviele von euch Grunzern muss ich noch zu Mammi und Daddi im Leichensack zurückschicken, bevor ihr endlich einseht, dass es Selbstmord ist mit dem Kopf voraus in eine Gruppe dieser mörderischen Legion zu laufen? Und nickt nicht mit euren dicken Schädeln als würdet ihr wissen wo von ich rede!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000056, 'Hey there, Belm! Give me a mug of Thunder Ale, and one for my good friend $N.', NULL, NULL, 'Hey, Belm! Gib mir einen Becher Donnerbier, und einen für meinen guten Freund $N.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000057, 'Spirits of Water, I give you praise, and I beg of you a favour. You have heard $N\'s pleas, and I trust his inent is noble. Please, will you aid us?', NULL, NULL, 'Geister des Wassers, ich preise euch und erflehe einen Gefallen von euch. Ihr habt die Bitten gehört, die $N an euch gerichtet hat, und ich bin mir sicher, ihre Beweggründe sind lauter und edel. Bitte, helft uns.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000058, 'Thank you great spirit. Thank you!', NULL, NULL, 'Dank Euch, großer Geist. Danke!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000060, 'By the stars! A spirit has been summoned!', NULL, NULL, 'Bei den Sternen! Ein Geist wurde beschworen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000061, 'It\'s a mystery of the past indeed! But a key to our future!', NULL, NULL, 'Es ist zwar ein Geheimnis der Vergangenheit! Aber es ist ein Schlüssel für unsere Zukunft!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000062, 'The threat be over! $N be savin\' us all!', NULL, NULL, 'Die Gefahr wird bald vorüber sein! $N wird uns alle beschützen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000063, 'Follow me, good $C. I\'ll have your barding done faster than I could down a Dwarven stout. Mmmm... stout.', NULL, NULL, 'Folgt mir, lieber $C. Ich werde euer Pferdegeschirr schneller fertig gestellt haben, als ich einen stämmigen Zwerg bezwingen kann. Mmmm... stämmig.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000064, 'Well, that should do it. Come now, back to the shop to finish our business!', NULL, NULL, 'Gut, das sollte seinen Zweck erfüllen. Kommt nun, lasst uns zurück zu dem Verkaufsstand gehen, um unser Geschäft abzuschließen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000065, 'We will suffer no demon\'s servant in our lands!', NULL, NULL, 'Wir werden keine Dämonendiener in unsere Ländern dulden!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000066, 'Peace and patience be with you, $N. Remain strong always.', NULL, NULL, 'Friede und Geduld sei mit Euch, $N. Bleibt immer stark.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000067, 'What are you doing in Ysera\'s realm, interloper? Leave us, I say! Your kind are not welcome here!', NULL, NULL, 'Was macht ihr im Reich von Ysera, Eindringling? Fort von hier, sage ich! Eure Sorte ist hier nicht willkommen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000068, 'You -- you\'re not one of them! Let\'s get out of sight before they find us! Hurry!', NULL, NULL, 'Ihr -- Ihr seid nicht einer von ihnen! Lasst und hier verschwinden bevor sie uns finden! Beeilt Euch!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000069, 'They\'re following me, $R! Quickly, tell me why you\'re here before we\'re discovered! If they catch up to me, it will mean my death and yours!', NULL, NULL, 'Sie verfolgen mich, $R! Schnell, erzählt mir warum Ihr hier seid bevor wir entdeckt werden! Wenn sie mich fangen, wird das meinen Tod bedeuten und den Euren ebenfalls!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000070, 'It would\'ve taken years to find the Book of the Raven without you, young sparrowhawk. Return to your home in Nagrand with our gratitude.', NULL, NULL, 'Es hätte ohne Euch Jahre gedauert das Buch des Raben zu finden, junger Sperber. Kehrt mit unserer Dankbarkeit in Eure Heimat nach Nagrand zurück.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000071, 'This is it! This is really it -- the Book of the Raven!', NULL, NULL, 'Das ist es! Das ist es wirklich -- Das Buch des Raben!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000072, 'It\'s all here... how the raven\'s followers captured and subdued the sprints of rivals, trapping them forever within stone statues! The tablet even mentions the raven god\'s name -- Anzu. I\'d thought it was lost to history!', NULL, NULL, 'Es ist alles hier... wie die Verfolger der Raben die Sprinter der Rivalen gefangen und unterworfen haben und sie für immer in Steinstatuen gefangen genommen haben! Die Schreibtafel erwähnt sogar den Namen des Rabengottes -- Anzu. Ich hätte gedacht dieser wäre im Laufe der Zeit verloren gegangen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000073, 'There\'s something else here, too. After a discription of a bloody battle and the downfall of the raven, there\'s a prophecy. Anzu will haunt the dreams of his enemies, twisting them to affect a return to this world...', NULL, NULL, 'Hier ist sogar noch etwas. Nach der Beschreibung eines blutigen Krieges und dem Untergang der Raben ist hier eine Prophezeiung. Anzu wird seine Feinde in deren Träumen heimsuchen und sie dazu überreden ihm die Rückkehr in diese Welt zu ermöglichen...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000074, 'The tablet has the confirmation we need, just as I thought. Now, we have to use this information to draw out and defeat this Anzu before he can do any more damage to the Emerald Dream!', NULL, NULL, 'Die Schreibtafel gibt uns die Bestätigung, die wir benötigen, genau wie ich dachte. jetzt müssen wir diese Informationen gebrauchen, um auszuziehen und Anzu zu besiegen bevor er dem smaragdgrünen Traum noch mehr Schaden zufügen kann!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000075, 'Since the sparrowhawks have proven such a success in hunting down the raven stones, Watcher Elaira has trained another one to relay my voice at a great distance. He can help us communicate as you investigate the bird spirits revealed in the tablet!', NULL, NULL, 'Seit die Sperber so einen großen Erfolg bei der Jagd nach den Rabensteine verbucht haben, hat Behüterin Elaira einen Weiteren trainiert, um meine Stimme über eine große Distanz zu übertragen. Er kann uns helfen miteinander zu kommunizieren während ihr den Vogelgeist der in der Schreibtafel gefangen ist erkundet!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005000, 'Poor Dorius. If I ever get my hands on those Dark Irons, so help me...', NULL, NULL, 'Armer Dorius. Falls ich jemals diese Dunkeleisen erwische, dann helft mir...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005001, 'Opus wants some Cuergo\'s Gold... with worm.', NULL, NULL, 'Opus will etwas Gold von Cuergo... mit Wurm.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005002, 'Bijou lost! SQUAWK! *cough', NULL, NULL, 'Bijou ist verloren! KRÄCHZ! *hust*', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005003, 'Kibler is being investigated by G.E.T.A.', NULL, NULL, 'Kibler wird derzeit von der G.E.T.A. geprüft.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005004, 'Bijou knows where treasures are hidden.', NULL, NULL, 'Bijou weiß, wo die Schätze versteckt sind.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005005, 'Dragons controlling orcs!? SQUAWK!', NULL, NULL, 'Großdrachen kontrollieren Orcs!? KRÄCHZ!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005006, 'They all go to the Spire but none never come back... just like Bijou.', NULL, NULL, 'Alle gehen zur Spitze, aber keiner kommt zurück... genau wie Bijou.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005007, 'Ask Kibler about the treasure! SQUAWK!', NULL, NULL, 'Fragt Kibler nach dem Schatz! KRÄCHZ!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005008, 'SQUAWK!! *cough* *cough* I need to quit smoking!', NULL, NULL, 'KRÄCHZ!! *hust* *hust* Ich muss mit dem Rauchen aufhören!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005009, 'Well done, soldiers! At ease!', NULL, NULL, 'Gut gemacht, Soldaten! Rührt euch!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005010, 'Zug zug! Happy to help!', NULL, NULL, 'Zug zug! Freut sich zu helfen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005011, 'Yes right away momma!', NULL, NULL, 'Ja auf der Stelle Mama!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005012, 'Mysterious hoofprints, scorched shields, stray badges... how do we make sense of all this?', NULL, NULL, 'Geheimnisvolle Hufabdrücke, versengte Schilde, seltsame Abzeichen... wie passt das nur alles zusammen?', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005013, 'I don\'t think Drazzit\'s going to have any luck finding anyone to kill this raptor. I mean, he\'s already raised the reward twice...', NULL, NULL, 'Ich glaube kaum, dass Drazzit jemanden finden wird, der seine verdammten Raptoren jagt. Ich meine, er hat die Belohnung schon zweimal erhöht...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005014, 'But this the only building that fit Brogg! Goblins make buildings too small!', NULL, NULL, 'Aber dies ist das einzige Gebäude in das Brogg passt! Goblins machen die Gebäude viel zu klein!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005015, 'I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else!', NULL, NULL, 'Ich dachte wir haben über dies gesprochen, Brogg. Du kannst in diesem Gebäude nicht bleiben. Näturlich, du kannst darin wohnen, aber es ist kein Platz für irgendjemand Anderen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005016, 'Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here!', NULL, NULL, 'Ja, wir haben gesagt, dass Ihr hier bleiben könnt, aber dieses Gebäude ist unser Hauptquartier! All unsere Unterlagen und Pläne werden hier aufbewahrt!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005017, 'I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent!', NULL, NULL, 'Ich widerspreche nur ungern, aber wenn Ihr mich fragt, seid ihr Oger einfach nur zu groß! Ich sehe schon, heute werde ich Euch wohl nicht zu einem Umzug bewegen können. Aber glaubt nicht, dass Ihr auf ewig hier wohnen könnt!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005018, 'Ah, this must be him now... no?', NULL, NULL, 'Ah, das muss er jetzt sein... nein?', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005019, 'You might wanna stand back. Fish guttin\' is a dirty job.', NULL, NULL, 'Ihr solltet zurücktreten. Das Ausnehmen von Fischen ist eine schmutzige Arbeit.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005020, 'It\'s good to see you defending Theramore with pride, friend.', NULL, NULL, 'Es ist gut zu sehen, dass Ihr Theramore mit Stolz verteidigt, Freund.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005021, 'When you\'re off duty, let\'s go to the inn. I\'ll buy a round and we can reminisce about the good old days.', NULL, NULL, 'Lasst uns zum Gasthaus gehen, wenn Ihr Dienstschluss habt. Ich gebe eine Runde aus und wir können uns über die gute alte Zeit unterhalten.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005022, 'Wouldn\'t you like to bring those days back? I have some friends who know just the way to do that.', NULL, NULL, 'Sehnt Ihr Euch nicht auch die gute alte Zeit herbei? Ich habe da ein paar Freunde, die uns bei der Erfüllung dieses Wunsches behilflich sein können...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005023, 'Every item that I have for sale I acquired myself. You have my personal guarantee of authenticity.', NULL, NULL, 'Jeden Gegenstand, den ich verkaufe, habe ich selbst erlangt. Ihr habt meine persönliche Garantie auf Echtheit.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005024, 'It\'s getting away!', NULL, NULL, 'Es entkommt!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005025, 'Prizes! Get your prizes right here! Get over here and turn in your Darkmoon Faire Prize Tickets for valuable prizes!', NULL, NULL, 'Gewinne! Holt Eure Gewinne direkt hier ab! Kommt her und löst Eure Gewinnlose des Dunkelmond-Jahrmarkts gegen wertvolle Gewinne ein!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005026, 'Come speak with me, and what once was cloudy shall become crystal clear.', NULL, NULL, 'Kommt sprecht mit mir, und was einst verschwommen war soll kristallklar werden.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005027, 'The Lich King\'s forces are building. It is imperative that our timetable supports his plans.', NULL, NULL, 'Die Macht des Lichkönigs wird stärker. Es ist von äußerster Wichtigkeit, dass unser Zeitplan seinem Vorhaben entspricht!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005028, 'Tomorrow we will begin training of our promising dragons, so don\'t forget your chew toys.', NULL, NULL, 'Morgen werden wir mit dem Training unserer vielversprechenden Drachen beginnen, also vergesst Eure Kauspielzeuge nicht.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005029, 'Our oldest clutch of dragons are still far from maturity, but with patience and study, we are confident the dragons will soon be ready.', NULL, NULL, 'Unser ältesten Gelege von Drachen sind noch immer weit von der Reife entfernt, aber mit Geduld und Studien sind wir überzeugt, dass die Dachen bald fertig sein werden.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005030, 'If you wait to come see me, there may be nothing left!', NULL, NULL, 'Wenn Ihr wartet um mich zu sehen, dann wird vielleicht nichts übrig sein!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005031, 'I come from the land down under... where women plague and men sunder.', NULL, NULL, 'Ich komme aus dem Land weit im Süden... wo die Frauen umtreiben und die Männer sich zerreisen.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005032, '...And then the tauren said, "13 INCHES!"', NULL, NULL, '...Und dann sagte der Taure, "33 ZENTIMETER!"', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005033, 'Come one, come all! Welcome to the Darkmoon Faire! Do you crave adventure? Do you seek exotic and mysterious treasures? Then look no further! You, my friend, have come to the right place! Dive right in and take part in all that the Faire has to offer! We\'ll be at this location all week, so be sure to tell your friends and loved ones!', NULL, NULL, 'Herbei, herbei! Willkommen auf dem Dunkelmond-Jahrmarkt! Ist euch nach Abenteuern zumute? Seid ihr auf der Suche nach mystischen, exotischen Schätzen? Dann sucht nicht weiter, denn wir haben alles was ihr begehrt! Taucht ein in eine Welt voller Wunder und erlebt was der Jahrmarkt zu bieten hat! Wir gastieren hier noch die ganze Woche über. Vergesst also nicht euren Freunden und Lieben Bescheid zu sagen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005034, 'Welcome one and all to the Darkmoon Faire, the greatest event in all the worlds! We have it all... delicious food, strong drinks, exotic artifacts, fortunes read, amazing prizes and excitement without end! Don\'t forget to turn in your Darkmoon Faire Prize Tickets to Gelvas Grimegate! All it takes is five or more and you\'re on your way to the most wondrous prizes in all of Outland. Everybody is a winner', NULL, NULL, 'Der einzigartige Dunkelmond-Jahrmarkt heißt euch willkommen! Hier findet ihr alles was das Herz begehrt... herzhaftes Essen, gutes Bier, exotische Artefakte und Tränke! Vielleicht sogar ein Blick in die Zukunft! Fantastische Preise und Vergnügen ohne Ende erwarten euch! Vergesst nicht eure Dunkelmond-Gewinnlose bei Gelvas Rußgatter einzulösen! Mit nur fünf Losen oder mehr seid ihr dabei und könnt zwischen den wunderbarsten Preisen in ganz Azeroth wählen. Bei uns ist jeder ein Gewinner!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005035, 'It\'s getting away!', NULL, NULL, 'Es entkommt!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005036, 'Please! I must feed on something soon... A mana crystal... a shard... anything! The pain is unbearable!', NULL, NULL, 'Bitte! Ich muss bald etwas fressen... Einen Manakristall... einen Splitter... irgendetwas! Der Schmerz ist unerträglich!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005037, 'I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head.', NULL, NULL, 'Vielleicht sollte ich einfach auf die Jagd nach dieser elenden Bestie gehen, jetzt wo eine Belohnung auf ihren Kopf ausgesetzt ist.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005038, 'Tell us! Where does you leader hide?', NULL, NULL, 'Redet! Wo versteckt sich Euer Anführer?', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005039, 'The wind speaks of our enemies\' movements, Ashyen. The Naga will attack again.', NULL, NULL, 'Der Wind berichtet von den Bewegungen unserer Gegner, Ashyen. Die Naga werden erneut angreifen.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005040, 'Very well. When that decision is made, I will be ready. Let us hope it won\'t be too late.', NULL, NULL, 'Sehr gut. Wenn diese Entscheidung getroffen wurde, werde ich bereit sein. Lasst uns hoffen, dass es nicht zu spät sein wird.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005041, 'I will do my best to take care of the children, Grandmother.', NULL, NULL, 'Ich werde mein Bestes geben um auf die Kinder aufzupassen, Großmutter.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005042, 'New posting up! Adventurers and heroes, gather round the bulletin board!', NULL, NULL, 'Neues Plakat angebracht! Abenteurer und Helden, versammelt euch um das schwarze Brett.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005043, 'I WILL DESTROY ALL', NULL, NULL, 'ICH WERDE ALLES ZERSTÖREN', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005044, 'US ALREADY ALL ANYMORE AND ANYMORE', NULL, NULL, 'UNS ALLE, MEHR UND MEHR', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005045, 'Just don\'t ask me where I got it...', NULL, NULL, 'Fragt einfach nicht woher ich es habe...', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005048, 'Having a good time?', NULL, NULL, 'Habt Ihr eine schöne Zeit?', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005049, 'I have another reading from the nether.', NULL, NULL, 'Ich habe eine weitere Lesung aus dem Nether.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005050, 'Well hand it overand let\'s see what you got!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005051, 'Great Gazlowe!', NULL, NULL, 'Klasse, Gazlowe!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005052, 'Hey, you there. Want some candy?', NULL, NULL, 'Hey, Ihr da! Wollt Ihr ein paar Süßigkeiten?', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005053, 'Bip!', NULL, NULL, 'Bip!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005054, 'Bip!', NULL, NULL, 'Bip!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005055, 'Bip!', NULL, NULL, 'Bip!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005056, 'Right. We just busted our tails to haul all of this stuff up here, and you want us to work more?', NULL, NULL, 'Genau. Wir haben gerade unsere Rücken ruiniert um all diesen Kram hier hoch zu schleppen, und Ihr wollt, dass wir noch mehr arbeiten?', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005057, 'Crybaby! Crybaby!', NULL, NULL, 'Heulsuse! Heulsuse!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000005058, 'Baby wants her dolly!', NULL, NULL, 'Das Baby will seine Puppe!', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
