	(37955, 2, 0, 'Yes... feed my precious one! You\'re mine now!', 14, 0, 0, 0, 0, 16790, 'Blood-Queen Lana\'thel - SAY_MIND_CONTROL'),
	(37955, 3, 0, 'You thirst for blood!', 42, 0, 0, 0, 0, 0, 'Blood-Queen Lana\'thel - EMOTE_BLOODTHIRST'),
