	(14510, 3, 0, 'Bless you mortal for this release. Hakkar controls me no longer...', 14, 0, 100, 0, 0, 8423, 'marli SAY_DEATH'),
	(14515, 0, 0, 'Bethekk, your priestess calls upon your might!', 14, 0, 100, 0, 0, 8416, 'arlokk SAY_AGGRO'),
	(14515, 1, 0, 'Feast on $n, my pretties!', 14, 0, 100, 0, 0, 0, 'arlokk SAY_FEAST_PANTHER'),
	(14515, 2, 0, 'At last, I am free of the Soulflayer!', 14, 0, 100, 0, 0, 8412, 'arlokk SAY_DEATH'),
