	(14517, 2, 0, 'Finally ...death. Curse you Hakkar! Curse you!', 14, 0, 100, 0, 0, 8422, 'jeklik SAY_DEATH'),
	(14834, 0, 0, 'PRIDE HERALDS THE END OF YOUR WORLD. COME, MORTALS! FACE THE WRATH OF THE SOULFLAYER!', 14, 0, 100, 0, 0, 8414, 'hakkar SAY_AGGRO'),
	(14834, 1, 0, 'Fleeing will do you no good, mortals!', 14, 0, 100, 0, 0, 0, 'hakkar SAY_FLEEING'),
