-- Creature AI and loot flag ---
UPDATE `creature_template` SET `faction`=190, `npcflag`=1, `AIName`='SmartAI' WHERE `entry` IN (45650, 45655, 45657);

-- Conditions for AI with and without quests ---
DELETE FROM `conditions` WHERE `SourceGroup` IN (1, 4) AND `SourceEntry` IN (45650, 45655, 45657);

-- Script ---
DELETE FROM `smart_scripts` WHERE `entryorguid` IN (45650, 45655, 45657);
